package day4;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=7;
		int num2=5;
		int res=num1+num2;
		int res1=num1-num2;
		int res2=num1*num2;
		int res3=num1/num2;
		int res4=num1%num2;
		int num=7;
		int s=7;
		int result=num++; //first it will fetch the num value.(post increment)
		int resu=++s; //first it will increment the num value.(past increment)
		System.out.println(res);
		System.out.println(res1);
		System.out.println(res2);
		System.out.println(res3);
		System.out.println(res4);
		System.out.println(result);
		System.out.println(resu);

	}

}
